import React, { useState } from 'react'
import axios from 'axios'
import '../AddAdmin/AddAdmin.css'
import { useNavigate } from 'react-router-dom';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';
function AddAdmin() {
 
  const navigate = useNavigate();
  const [errMsg,setErrMsg]=useState('')
  const [adminData,setAdminData]=useState({})

  const handleChange=(e)=>{
    setAdminData({...adminData,[e.target.name]:e.target.value}) 
  }
  const validationSchema = Yup.object().shape({
    name: Yup.string().required('Admin Name is required'),
    email: Yup.string().email('Invalid email').required('Admin Email is required'),
    password: Yup.string().required('Password is required'),
  });

    const addAdmin=async(e)=>{
    e.preventDefault()
    try{
      const response = await axios.post('http://localhost:8000/api/admin/register', adminData);
      
      navigate('/admin/profile')
    }catch(result){
      setErrMsg(result.response?.data.message)
  }
}
  return (
    <>
       <section className="container">
        <div className="add-admin-box">
            <h2>Register Admin</h2>
            {
              errMsg? <p className='err-msg'>{errMsg}</p> : ''
            }
            
            <div className="add-admin">
            <Formik
      initialValues={initialValues}
      validationSchema={validationSchema}
      onSubmit={AddAdmin}
    >
      <Form>
        <Field type="text" name="name" className="login-input" placeholder="Admin Name" />
        <ErrorMessage name="name" component="div" className="error-message" />
        
        <Field type="email" name="email" className="login-input" placeholder="Admin Email" />
        <ErrorMessage name="email" component="div" className="error-message" />
        
        <Field type="password" name="password" className="login-input" placeholder="Enter New Password for Admin" />
        <ErrorMessage name="password" component="div" className="error-message" />
        
        <button type="submit" className="add-admin-btn">Add Admin</button>
      </Form>
    </Formik>
            </div>
        </div>
      </section>
    </>
  )
}


export default AddAdmin
