import React from 'react'
import '../SignUp/SignUp.css'
import { Link } from 'react-router-dom'
function SignUp() {
  const navigate = useNavigate();

  const { register, handleSubmit, formState: { errors } } = useForm();
  const [errMsg,setErrMsg]=useState('')
  const [,setAdminData]=useState({})
  return (
    <>
       <section className="container">
        <div className="signup-box">
            <h2>Sign Up </h2>
            
            <div className="mannual-signup">
                <form action="">
                    <input type="text" className="signup-input" placeholder='Your Name here' />
                    <input type="email" className="signup-input" placeholder='Your Email here' value={adminData.email} 
                    {...register('email',{ required: { value: true, message: 'Please fill the  Email Field' },
                    pattern: { 
                      value: /^[^\s@]+@[^\s@]+\.[^\s@]+$/, 
                      message: 'Please enter a valid email address' 
                    } 
                    })} onChange={(e)=>setAdminData({...adminData,email:e.target.value})}/>
                     <p className="err-msg">{errors?.email?.message}</p>

                    <input type="password" className="signup-input" placeholder='Create a Password' value={adminData.password} 
                    {...register('password', { required: { value: true, message: 'Please Enter a Password' } })}  
                    onChange={(e)=>setAdminData({...adminData,password:e.target.value})}/>
                    <p className="err-msg">{errors?.password?.message}</p>

                    <button className="signup-btn">Sign Up</button>
                    <button className="google-signin-button" >
                     <img src="https://cdn1.iconfinder.com/data/icons/google-s-logo/150/Google_Icons-09-512.png" alt="Google Logo" className="google-logo" />
                     Continue with Google
                   </button>
                   <Link to="/login" className="login-link">Already Have an Account?</Link>
                </form>
            </div>
           
           
        </div>
      </section>
    </>
  )
}

export default SignUp
