import React, { useState } from 'react'
import '../EditDeal/EditDeal.css'
import { useParams } from 'react-router-dom'
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import {faPlus,faTrash} from '@fortawesome/free-solid-svg-icons'
import { useEffect } from 'react'
import axios from 'axios'

function EditDeal() {
  const [photo, setImage]=useState(null)
  const { dealId } = useParams();
  const [dealDetails,setDealdetails]=useState([])

  let imageUrl= require(`../../uploads/deals`)
  useEffect(() => {
    // Function to fetch deal details or perform other side effects
 setImage(imageUrl)
    const fetchDealDetails = async () => {
        try {
            const response = await axios.get(`http://localhost:8000/api/topDeals/${dealId}`);
            setDealdetails(response.data)
            console.log(dealDetails); // Handle the fetched data
        } catch (error) {
            console.error('Error fetching deal details:', error);
        }
    };

    // Call the function to fetch deal details
    fetchDealDetails();
}, []);

  
 
  // Function to handle when a new image is selected
  const handleImageChange = (e) => {
    const selectedImage = e.target.files[0];
    imageUrl=URL.createObjectURL(selectedImage)
    
    setImage(imageUrl);
  };
 
  const clearImages=()=>{
    setImage(null)
  }
 const renderImage=()=>{
  if (photo) {
    return <img src={photo} alt="deal Image" className='selected-img' />;
}
return null;
 }
  


  return (
    <>
    <section className="admin-deal-section">
    <div className="edit-deal">
     <h2>Edit Deal</h2>
     {
     <div className="row">
     <div className="col-md-6 col-sm-12">
     <input type="text" className="deal-input" placeholder='From' value={dealDetails.from} />
     <input type="text" className="deal-input" placeholder='To' value={dealDetails.to} />
     <div className="flex">
     <input type="text" className="deal-input halved-input" placeholder='Price'  value={dealDetails.price} />
     <input type="text" className="deal-input halved-input" placeholder='Baggage'  value={dealDetails.baggage}/>
     </div>
     <input type="text" className="deal-input duration-input" placeholder='airline'  value={dealDetails.airline}/>
     <textarea name="description" id=""className='deal-input description-input' placeholder='Description'  value={dealDetails.description}></textarea>

     <div className="flex">
     <label htmlFor="fileInput" className="custom-file-selector-button">
     <FontAwesomeIcon icon={faPlus}  className="plus-icon"/>
     <span>Select Image</span>
     <input 
        id="fileInput"
        type="file" 
        accept="image/*" 
        onChange={handleImageChange}
/>
     </label>{
       photo?<div onClick={clearImages} className='dlt-image'>
                 <FontAwesomeIcon icon={faTrash} className='trash-icon' />
                 <p>Delete</p>
                 </div>  
                 :''
     }
     </div>
    
       
        {renderImage() }
       

    <button className='add-btn'>Submit Edits</button>
    <button className='cancel-btn'>Cancel</button>
     </div>
    </div>
}
    </div>
    </section>

   
    </>
  )


  
}

export default EditDeal
