// AuthContext.js
import React, { createContext, useState, useContext, useEffect } from 'react';
import axios from 'axios'
const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [user,setUser]=useState(null)
    useEffect(() => {
       

  useEffect(()=>{
    axios.get('http://localhost:8000/api/auth/user', { withCredentials: true }).then((response)=>{
      console.log('response'+response)
      if(response.status===200){
        console.log('status 200')
        setUser(response.data)
        console.log(user)
      }
     setUser(response.data)
     console.log(response.data)
    }).catch((error)=>{
      console.log(error)
      setUser(null)
    })
  },[])
        setIsLoggedIn(!!user); // Update isLoggedIn state based on token presence
    }, []);

  
    const logout = () => {
        // Perform logout actions, e.g., clear JWT token from localStorage or cookies
        axios.get('http://localhost:8000/api/auth/user/logout', { withCredentials: true }).then((response)=>{
            console.log('response'+response)
            if(response.status===200){
              console.log('status 200')
              setUser(response.data)
              console.log(user)
            }
           setUser(response.data)
           console.log(response.data)
          }).catch((error)=>{
            console.log(error)
            setUser(null)
          })
        setIsLoggedIn(false);
    };

    return (
        <AuthContext.Provider value={{ isLoggedIn,logout ,user }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => useContext(AuthContext);
