import React, { useState } from 'react'
import '../EditDeal/EditDeal.css'
import { useNavigate, useParams } from 'react-router-dom'
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import {faPlus,faTrash} from '@fortawesome/free-solid-svg-icons'
import { useEffect } from 'react'
import axios from 'axios'
import { useForm } from 'react-hook-form'

function EditDeal() {
  const [photo, setImage]=useState(null)
  const { dealId } = useParams();
  const [dealDetails,setDealdetails]=useState([])
  const [errMsg,setErrMsg]=useState('')
  const { register, handleSubmit, formState: { errors } } = useForm();
  const navigate = useNavigate();
  const [imageUrl,setImageUrl]=useState('')


  let url= require(`../../uploads/deals/${dealId}.jpg`)
  

  useEffect(() => {
    // Function to fetch deal details or perform other side effects

    const fetchDealDetails = async () => {
        try {
            const response = await axios.get(`http://localhost:8000/api/topDeals/${dealId}`);
            setDealdetails(response.data)
            setImageUrl(url)
            console.log(dealDetails); // Handle the fetched data
        } catch (error) {
            console.error('Error fetching deal details:', error);
        }
    };

    // Call the function to fetch deal details
    fetchDealDetails();
}, []);

  
 
  // Function to handle when a new image is selected
  const handleImageChange = (e) => {
    const selectedImage = e.target.files[0];
    setImage(selectedImage);
    url = URL.createObjectURL(selectedImage)
    setImageUrl(url)
  };
 
  const clearImages=()=>{
    setImage(null)
  }
 const renderImage=()=>{
  
    return <img src={imageUrl} alt="deal Image" className='selected-img' />;
 }
 const handleChange=(e)=>{
  setDealdetails({...dealDetails,[e.target.name]:e.target.value})
  console.log(dealDetails)
  console.log(e.target.name+e.target.value)
 }
 
 const handleEdit = async (data) => {
 
  try {
    console.log(data)
    console.log(errors)
    const formData=new FormData()  
    formData.append('image', photo);
     const dealData={
      from:data.from,
      to:data.to,
      baggage:data.baggage,
      airline:data.airline,
      price:data.price,
      description:data.description
    }
  for (const key in dealData) {
    if (dealData.hasOwnProperty(key)) {
      formData.append(key, dealData[key]);
    }
  }
 
    for (let pair of formData.entries()) {
      console.log(pair[0] + ': ' + pair[1]);
    }
     axios.put(`http://localhost:8000/api/topDeals/${dealId}`, formData,{
      headers:{
        'Content-Type':'multipart/form-data'
      }
     }).then((res=>{
      console.log(res.data)
    })).catch(err=>{
      console.log(err)
      
    })
  // navigate('/admin/add-deals');
  } catch (error) {
    setErrMsg(error.response?.data.message);
  }
};
  


  return (
    <>
    <section className="admin-deal-section">
    <div className="edit-deal">
     <h2>Edit Deal</h2>
     {
      <form onSubmit={handleSubmit(handleEdit)} encType='multipart/form-data'>
     <div className="row">
     <div className="col-md-6 col-sm-12">
     {errMsg && <p className="package-err-msg">{errMsg}</p>}
     <input type="text" className="deal-input" placeholder="From" {...register('from', { required: true })} defaultValue={dealDetails.from}
     onChange={handleChange} name='from'/>
                {errors.from && <p className="deal-error-msg">Please enter the origin</p>}
                <input type="text" className="deal-input" placeholder="To" {...register('to', { required: true })} defaultValue={dealDetails.to}
     onchange={handleChange} name='to'/>
                {errors.to && <p className="deal-error-msg">Please enter the destination</p>}
                <div className="flex">
                  <div>
                  <input type="text" className="deal-input halved-input" placeholder="Price" {...register('price', { required: true })} defaultValue={dealDetails.price} 
     onchange={handleChange} name='price'/>
                  {errors.price && <p className="deal-error-msg">Please enter the price</p>}
                  </div>
                  <div>
                  <input type="text" className="deal-input halved-input" placeholder="Baggage" {...register('baggage',{ required: true })} defaultValue={dealDetails.baggage}
    onchange={handleChange} name='baggage'/>
                  {errors.baggage && <p className="deal-error-msg">Please enter the price</p>}
                  </div>
                </div>
                <input type="text" className="deal-input duration-input" placeholder="Airline" {...register('airline',{ required: true })} defaultValue={dealDetails.airline}
    onchange={handleChange} name='airline'/>
                {errors.airline && <p className="deal-error-msg">Please enter airline</p>}
                <textarea name="description" className="deal-input description-input" placeholder="Description" {...register('description',{ required: true })}
             onchange={handleChange}    defaultValue={dealDetails.description}></textarea>
                {errors.description && <p className="deal-error-msg">Please enter description</p>}

     <div className="flex">
     <label htmlFor="fileInput" className="custom-file-selector-button">
     <FontAwesomeIcon icon={faPlus}  className="plus-icon"/>
     <span>Select Image</span>
     <input 
        id="fileInput"
        type="file" 
        accept="image/*" 
        onChange={handleImageChange}
/>
     </label>{
       photo?<div onClick={clearImages} className='dlt-image'>
                 <FontAwesomeIcon icon={faTrash} className='trash-icon' />
                 <p>Delete</p>
                 </div>  
                 :''
     }
     </div>
     <div>
     {renderImage() }
     </div>
        
       

    <button className='add-btn' onClick={handleEdit}>Submit Edits</button>
    <button className='cancel-btn'>Cancel</button>
     </div>
    </div>
    </form>
}
    </div>
    </section>

   
    </>
  )


  
}

export default EditDeal
