import React, { useState } from 'react'
import axios from 'axios'
import '../AddAdmin/AddAdmin.css'
import { useNavigate } from 'react-router-dom';
function AddAdmin() {
 
  const navigate = useNavigate();

  const [adminData,setAdminData]=useState({
    adminName:'',
    adminEmail:'',
    adminPassword:'' 
  })

  const handleChange=(e)=>{
    setAdminData({...adminData,[e.target.name]:e.target.value})
  }

    const addAdmin=async(e)=>{
    e.preventDefault()
    try{
      const response = await axios.post('/api/admin/register', adminData);
      console.log(response)
    }catch(err){
       console.log(err)
  }
}
  return (
    <>
       <section className="container">
        <div className="add-admin-box">
            <h2>Admin Login</h2>
            <div className="add-admin">
                <form action="">
                  
                    <input type="Name" 
                    className="login-input" 
                    placeholder='Admin Name' 
                    onChange={handleChange} 
                    name='adminName' value={adminData.adminName}/>

                    <input type="email" 
                    className="login-input" 
                    placeholder='Admin Email' 
                    onChange={handleChange}
                    name='adminEmail' value={adminData.adminEmail}/>

                    <input type="password" 
                    className="login-input" 
                    placeholder='Enter New Password for Admin'  
                    onChange={handleChange} 
                    name='adminPassword' value={adminData.adminPassword}/>
                    <button className="add-admin-btn" onClick={addAdmin}>Add Admin</button>
                
                </form>
            </div>
        </div>
      </section>
    </>
  )
}


export default AddAdmin
