import React, { useState } from 'react'
import axios from 'axios'
import { useForm } from 'react-hook-form';
import '../AddAdmin/AddAdmin.css'
import { useNavigate } from 'react-router-dom';
function AddAdmin() {
  const {register,handleSubmit,errros}=useForm()
 
  const navigate = useNavigate();
  const [errMsg,setErrMsg]=useState('')
  const [adminData,setAdminData]=useState({})

  const handleChange=(e)=>{
    setAdminData({...adminData,[e.target.name]:e.target.value}) 
  }

    const addAdmin=async(e)=>{
    e.preventDefault()
    try{
      const response = await axios.post('http://localhost:8000/api/admin/register', adminData);
      
      navigate('/admin/profile')
    }catch(result){
      setErrMsg(result.response?.data.message)
  }
}
  return (
    <>
       <section className="container">
        <div className="add-admin-box">
            <h2>Register Admin</h2>
            {
              errMsg? <p className='err-msg'>{errMsg}</p> : ''
            }
            
            <div className="add-admin">
                <form action="">
                  
                    <input type="Name" required
                    className="login-input" 
                    placeholder='Admin Name' 
                    onChange={handleChange} 
                    name='name' value={adminData.name} ref={register}/>

                    <input type="email" required
                    className="login-input" 
                    placeholder='Admin Email' 
                    onChange={handleChange}
                    name='email' value={adminData.email} ref={register}/>

                    <input type="password" required
                    className="login-input" 
                    placeholder='Enter New Password for Admin'  
                    onChange={handleChange} 
                    name='password' value={adminData.password} ref={register}  />
                    <button className="add-admin-btn" onClick={addAdmin}>Add Admin</button>
                
                </form>
            </div>
        </div>
      </section>
    </>
  )
}


export default AddAdmin
