import React, { useEffect, useState } from 'react'
import '../EditPackage/EditPackage.css'
import axios from 'axios'
import { useForm } from 'react-hook-form'
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import {faPlus,faTrash,faArrowUpFromBracket,faPen} from '@fortawesome/free-solid-svg-icons'
import { useParams ,useNavigate} from 'react-router-dom'
function EditPackage() {

  useEffect(() => {
    // Function to fetch package details or perform other side effects

    const fetchPackageDetails = async () => {
        try {
            const response = await axios.get(`http://localhost:8000/api/package/${packageId}`);
            setPackageDetails(response.data)
            
            console.log('packageData'+packageDetails); // Handle the fetched data
        } catch (error) {
            console.error('Error fetching deal details:', error);
        }
    };

    // Call the function to fetch deal details
    fetchPackageDetails();
    setSelectedOptions(packageDetails.activities)
    const imageDest=require(`../../uploads/packages/${packageId}`)
    setimages()
}, []);

  const { handleSubmit } = useForm();
  const navigate=useNavigate()
  const [packageDetails,setPackageDetails]=useState({})
  const { packageId } = useParams();
  const [images, setImages] = useState([]);
  const [selectedOptions, setSelectedOptions] = useState([]);
  const [dropdownOpen, setDropdownOpen] = useState(false);
  // Function to handle when a new image is selected
  const handleImageChange = (e) => {
    const selectedImages = Array.from(e.target.files);
    setImages([...images, ...selectedImages]);
  };
 
  const clearImages=(e)=>{
    setImages([])
  }
  const renderImagePreviews = () => {
    return images.map((image, index) => (
      <div key={index}>
        <img 
          src={URL.createObjectURL(image)} 
          alt={`Uploaded ${index}`} 
          style={{ maxWidth: '100px', maxHeight: '100px', marginRight: '10px' }} 
          className='preview-img '
        />
      </div>
    ));
  };
  

  const options = ['Option 1', 'Option 2', 'Option 3', 'Option 4', 'Option 5', 'Option 6', 'Option 7', 'Option 8'];

 

  const handleSelectOption = (e) => {
    const option = e.target.value;
    console.log(option);
    const isExist = selectedOptions.includes(option);
    console.log(isExist + selectedOptions);
    if (!isExist && option !== "") {
      setSelectedOptions([...selectedOptions, option]);
    }
  };
  const clearActivities=()=>{
    setSelectedOptions([])
  }

  const handleEdit = async (data) => {
   
    try {
      console.log('ssubmitting')
      console.log(data)
      
      const formData=new FormData()  
      data.from=packageDetails.name
      data.to=packageDetails.destination
      data.baggage=packageDetails.price
      data.duration={days:packageDetails.duration.days,nights:packageDetails.duration.nights}
      
      data.description=packageDetails.description
      console.log('printing data '+data)
      images.forEach((image, index) => {
        formData.append('images', image);
    })
       
    for (const key in data) {
      if (data.hasOwnProperty(key)) {
        formData.append(key, data[key]);
      }
    }
   
      for (let pair of formData.entries()) {
        console.log(pair[0] + ': ' + pair[1]);
      }
       axios.put(`http://localhost:8000/api/topDeals/${packageId}`, formData,{
        headers:{
          'Content-Type':'multipart/form-data'
        }
       }).then((res=>{
        console.log(res.data)
        alert(res.data.message)
        navigate('/admin/add-deals')
      })).catch(err=>{
        console.log(err)
        alert('something Went Wrong , Try Again')
        navigate('/admin/add-deals')
      })
    } catch (error) {
      alert(error.response?.data.message);
    }
  };
    
  
  
  return (
    <>
    <section className="admin-package-section">
    <div className="edit-package">
     <h2>Edit Package</h2>
     <form action="" onSubmit={handleSubmit(handleEdit)} encType='multipart/form-data'>
     <div className="row">
     <div className="col-md-6 col-sm-12">
     <input type="text" className="package-input" placeholder='Package Name ' value={packageDetails.name}
     onChange={e=>setPackageDetails({...packageDetails,name:e.target.value})}/>
     <input type="text" className="package-input" placeholder='Destination' value={packageDetails.destination}
     onChange={e=>setPackageDetails({...packageDetails,destination:e.target.value})}/>
     <p>Duration</p>
     <div className="flex">
     <input type="text" className="package-input duration-input" placeholder='Days' value={packageDetails.duration?.days}
     onChange={e=>setPackageDetails({...packageDetails,duration:{days:e.target.value}})}/>
     <input type="text" className="package-input duration-input" placeholder='Nights' value={packageDetails.duration?.nights}
     onChange={e=>setPackageDetails({...packageDetails,duration:{nights:e.target.value}})}/>
     </div>
     <input type="text" className="package-input" placeholder='Price' value={packageDetails.price}
     onChange={e=>setPackageDetails({...packageDetails,price:e.target.value})}/>
     <textarea name="description" id=""className='package-input description-input' placeholder='Description'></textarea>
     <button className='add-btn' type='submit'>Submit Edits</button>
     <button className='cancel-btn' >Cancel</button>
     </div>

     <div className="col-md-6 col-sm-12">
     <div className="flex">
     <label htmlFor="fileInput" className="custom-file-selector-button">
     <FontAwesomeIcon icon={faPlus}  className="plus-icon"/>
     <span>Select Images</span>
     <input 
  id="fileInput"
  type="file" 
  accept="image/*" 
  multiple 
  onChange={handleImageChange}
/>
     </label>{
       images[0]?<div onClick={clearImages} className='dlt-images'>
                 <FontAwesomeIcon icon={faTrash} className='trash-icon' />
                 <p>Clear All </p>
                 </div>  
                 :''
     }
     </div>
     {
       images[0]?<div className="image-preview-div  ">
                {renderImagePreviews()}
                </div>:''
     }
     <select  onChange={handleSelectOption}>
        <option value="">Select Activities</option>
        {options.map((option, index) => (
          <option key={index} value={option} name={option}>
            {option}
          </option>
        ))}
      </select>
      <div className="selected-options ">
        <ul>
          {selectedOptions?.map((option, index) => (
            <li className='options' key={index}>{option}</li>
          ))}
        </ul>
      </div>
      {
       selectedOptions?<div onClick={clearActivities} className='dlt-activities'>
                 <FontAwesomeIcon icon={faTrash} className='trash-icon' />
                 <p>Clear All </p>
                 </div>  
                 :''
     }
      
     </div>
    </div>
    </form>
    </div>
      
    
    </section>

     
    </>
  )
}

export default EditPackage
