// AuthContext.js
import React, { createContext, useState, useContext, useEffect } from 'react';
import axios from 'axios'
const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [user,setUser]=useState(null)
  

  
    const logout = () => {
        
        const handleLogout = async () => {
            try {
               
                await axios.get('http://localhost:8000/api/auth/user/logout')
            } catch (error) {
                console.error('Logout failed:', error);
            }
        };
        handleLogout();
        setIsLoggedIn(false);
    };
   const checkLoginStatus=()=>{
    const check=()=>{
        
    }
      axios.get('http://localhost:8000/api/auth/user', { withCredentials: true }).then((response)=>{
      

      if(response.data.message){
        return true
      }else{
        return false
      }
 }).catch((error)=>{
   console.log(error)
   setUser(null)
 })
   }
    return (
        <AuthContext.Provider value={{ isLoggedIn,logout ,user,checkLoginStatus }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => useContext(AuthContext);
